import React from "react";
import { Card } from "react-bootstrap";
import Rating from "../components/Rating";
import { Link } from "react-router-dom";
import styles from "./ProductScreen.module.css"; // Import the CSS module

const ProductScreen = ({ product }) => {
  return (
    <>
      <Card className={`m-3 p-3 rounded ${styles.card}`}>
        <Link to={`/product/${product._id}`}>
          <Card.Img
            src={product.image}
            variant="top"
            className={styles.cardImage} // Add the custom CSS class
          />
        </Link>
        <Card.Body className={styles.cardBody}> {/* Apply the new CSS class */}
          <Link to={`/product/${product._id}`}>
            <Card.Title as="div">
              <strong>{product.name}</strong>
            </Card.Title>
          </Link>
          <Card.Text as="div">
            <Rating
              value={product.rating}
              text={`${product.numReviews} reviews`}
            />
          </Card.Text>
          <Card.Text as="div">$ {product.price}</Card.Text>
        </Card.Body>
      </Card>
    </>
  );
};

export default ProductScreen;
